import React, { useRef, useState, useContext } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCloudUploadAlt } from "@fortawesome/free-solid-svg-icons";
import "../styles/upload-tourguide.css";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "./../context/AuthContext";
import { BASE_URL } from "./../utils/config";

const UploadTourguide = () => {
  const fileInputRef = useRef(null);
  const [selectedFileName, setSelectedFileName] = useState("");
  const AddTourguide = () => {
    const [title, setTitle] = useState("");
    const [city, setCity] = useState("");
    const [photo, setPhoto] = useState("");
    const [price, setPrice] = useState("");
    const [desc, setDesc] = useState("");
    const navigate = useNavigate();

    const saveTourguide = async (e) => {
      e.preventDefault();
      try {
         await fetch(`${BASE_URL}/auth/register`, {
          title,
          city,
          photo,
          price,
          desc,
        });
        
        navigate("/tours");
      } catch (err) {
        console.log(err);
      }
    };

    // const [tourData, setTourdata] = useState({
    //   title: undefined,
    //   city: undefined,
    //   photo: undefined,
    //   price: undefined,
    //   desc: undefined,
    // });

    // const { dispatch } = useContext(AuthContext);
    // const navigate = useNavigate();

    // const handleFileUploadClick = () => {
    //   fileInputRef.current.click();
    // };

    const handleFileChange = (event) => {
      const file = event.target.files[0];
      setSelectedFileName(file.name);
    };

    // const handleChange = (e) => {
    //   setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    //   console.log(e.target.id);
    // };

    // const handleClick = async (e) => {
    //   e.preventDefault();
    //   console.log(credentials);
    //   try {
    //     const res = await fetch(`${BASE_URL}/tourguide`, {
    //       method: "post",
    //       headers: {
    //         "content-type": "application/json",
    //       },
    //       body: JSON.stringify(credentials),
    //     });
    //     const result = await res.json();

    //     if (!res.ok) alert(result.message);

    //     dispatch({ type: "LOGIN_SUCCESS" });
    //     navigate("/tours");
    //   } catch (err) {
    //     alert(err.message);
    //   }
    // };
    return (
      <div className="body">
        <h3 className="Judul__TourGuide">
          Ayo Daftarkan Tour Guide Anda <br />
          untuk menjadi bagian dari TOUR'U
        </h3>
        <form
          action=""
          className="d-flex flex-column justify-content-center items-center gap-5"
          onSubmit={saveTourguide}
        >
          <div className="d-flex justify-content-center gap-5">
            <div className="form-kiri">
              <input
                className="form-control form-control-lg"
                type="text"
                placeholder="Nama Tourguide"
                required
                id="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
              <input
                className="form-control form-control-lg"
                type="text"
                placeholder="Lokasi"
                required
                id="city"
                value={city}
                onChange={(e) => setCity(e.target.value)}
                // onChange={handleChange}
              />
              <input
                className="form-control form-control-lg"
                type="text"
                placeholder="Harga/Hari"
                required
                id="price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                // onChange={handleChange}
              />
              <input
                className="form-control form-control-lg"
                type="text"
                placeholder="Deskripsi"
                required
                id="desc"
                value={desc}
                onChange={(e) => setDesc(e.target.value)}
                // onChange={handleChange}
              />
            </div>
            <label className="upload-label" htmlFor="formFileLg">
              <span>{selectedFileName || "Upload Foto Diri"}</span>
              <FontAwesomeIcon
                icon={faCloudUploadAlt}
                className="upload-icon"
              />
              <input
                ref={fileInputRef}
                className="form-control form-control-lg"
                type="file"
                id="formFileLg"
                accept="image/pdf*"
                onChange={(handleFileChange, (e) => setPhoto(e.target.value))}
                value={photo}
              />
            </label>
          </div>
          <div className="m-auto d-flex gap-3">
            <button
              className="button-submit m-auto rounded-pill"
              type="submit"
              // onChange={handleChange}
            >
              Submit
            </button>
            <a className="cancel m-auto rounded-pill" href="/tours">
              Cancel
            </a>
          </div>
        </form>
      </div>
    );
  };
};

export default UploadTourguide;
