import React, { useEffect, useRef, useState } from "react";
import "../styles/destinasi-details.css";
import { Container, Row, Col, ListGroup } from "reactstrap";
// import foto from "../assets/images/destinasi.png";
import { useParams } from "react-router-dom";
// import tourData from "../assets/data/tours";
import calculateAvgRating from "./../utils/avgRating";
import avatar from "../assets/images/avatar.jpg";
import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

const DestinasiDetails = () => {
  const { id } = useParams();
  // fetch data from database
  const {
    data: destination,
    loading,
    error,
  } = useFetch(`${BASE_URL}/destination/${id}`);
  // DESTRUCTURE PROPERTIES DARI OBJEK homestay
  const {
    photo,
    title,
    desc,
    desc1,
    desc2,
    desc3,
    price,
    address,
    reviews,
    city,
    maxGroupSize,
  } = destination;

  //format date
  const options = { day: "numeric", month: "long", year: "numeric" };

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [destination]);

  return (
    <>
      <section>
        <Container>
          {loading && <h4 className="text-center pt-5">Loading.....</h4>}
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && (
            <Row>
              <Col>
                <div className="tour__content">
                  <img src={photo} alt="" />
                  <div className="tour__info">
                    <h2>{title}</h2>
                    <div className="= d-flex align-items-center gap-5"></div>
                    <span>
                      <i class="ri-map-pin-line"></i> {address}
                    </span>
                    <div className="tour__extra-details">
                      <span>Rp.{price}/orang</span>
                    </div>
                    <h5>Deskripsi</h5>
                    
                    <p>
                      Kawah Putih adalah sebuah tempat wisata alam di Jawa Barat
                      yang terletak di Desa Alam Endah, Kecamatan Rancabali,
                      Kabupaten Bandung, Jawa Barat atau tepatnya di kaki Gunung
                      Patuha. Kawah Putih ini merupakan sebuah danau yang
                      terbentuk dari letusan Gunung Patuha. Sesuai namanya,
                      tanah yang ada dikawasan ini berwarna putih akibat dari
                      pencampuran unsur belerang. Selain itu, air danau kawasan
                    </p>
                    <p>
                      Kawah Putih Bandung juga mempunyai warna yaang putih
                      kehijauan dan dapat berubah warna sesuai dengan kadar
                      belerang yang terkandung, suhu, dan cuaca. Kawah Putih
                      Bandung berada di kawasan pegunungan yang mempunyai
                      ketinggian lebih dari 2.400 meter di atas permukaan laut.
                      Dengan ketinggian tersebut, suhu udara di tempat wisata
                      ini mencapai 8 derajat sampai 22 derajat celcius.
                    </p>
                  </div>
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
    </>
  );
};

export default DestinasiDetails;
