import React from "react";
import CommonSectionDestination from "../shared/CommonSectionDestination";

// import "../styles/destinasi.css";
import tourData from "../assets/data/tours";
import DestinasiCard from "./../shared/DestinasiCard";
import SearchBar from "./../shared/SearchBar";
import { Container, Row, Col } from "reactstrap";
import { Link } from "react-router-dom";

import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

const Destinasi = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  const {
    data: destination,
    loading,
    error,
  } = useFetch(`${BASE_URL}/homestays?page=${page}`);
  const { data: destinationCount } = useFetch(
    `${BASE_URL}/destination/search/getHomeStayCount`
  );

  useEffect(() => {
    const pages = Math.ceil(homestaysCount / 8);
    setPageCount(pages);
    window.scrollTo(0, 0);
  }, [page, homestaysCount, destination]);
  return (
    <>
      <CommonSectionDestination title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <h2 className="tourtitle">Destinasi</h2>
            <div className="horizontal"></div>
          </Row>
          <Row className="top">
            <Col lg="7">
              <div className="pilih">
                <p>Pilih lokasi yang kamu inginkan!</p>
              </div>
              <SearchBar />
            </Col>
          </Row>
        </Container>
      </section>

      <section>
        <Container>
          <Row>
            {tourData?.map((tour) => (
              <Col lg="3" className="mb-5" key={tour.id}>
                <DestinasiCard tour={tour} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>
    </>
  );
};

export default Destinasi;
