import React, { useState, useEffect } from "react";
import CommonSection from "../shared/CommonSectionTour";

import "../styles/tour.css";
import TourCard from "../shared/TourCard";
import SearchBar from "../shared/SearchBar";
import tourData from "../assets/data/tours";
import { Container } from "reactstrap";
import { Row, Col } from "reactstrap";
import { Link } from "react-router-dom";

import useFetch from '../hooks/useFetch'
import { BASE_URL } from "../utils/config";

const Tours = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  const{data:tours , loading, error} = useFetch(`${BASE_URL}/TourGuide`)
  const { data: toursCount } = useFetch(
    `${BASE_URL}/TourGuide/search/getTourGuideCount`
  );


  useEffect(() => {
    const pages = Math.ceil(toursCount / 4);
    setPageCount(pages);
  }, [page,toursCount]);

  return (
    <>
      <CommonSection />
      <section>
        <Container>
          <Row>
            <h2 className="tourtitle"> Tour Guide</h2>
            <div className="horizontal"></div>
          </Row>
          <Row className="top">
            <Col xxl="7">
              <div className="pilih">
                <p>Pilih Tour Guide yang kamu inginkan!</p>
              </div>
              <SearchBar />
            </Col>

            <Col xxl="5">
              <div className="pilih1">
                <p>Apakah kamu mau mendaftarkan Tour Guide?</p>
              </div>
              <div className="daftar">
                <Link>Daftar menjadi Tour Guide</Link>
              </div>
            </Col>
          </Row>
        </Container>
      </section>

      <section className="pt-0">
        <Container>
          {
            !loading && !error &&

          <Row>
            {tours?.map((tour) => (
              <Col lg="3" className="mb-4" key={tour.id}>
                <TourCard tour={tour} />
              </Col>
            ))}

            <Col lg="12">
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount).keys()].map((number) => (
                  <span
                    key={number}
                    onClick={() => setPage(number)}
                    className={page === number ? "active__page" : ""}
                  >
                    {number + 1}
                  </span>
                ))}
              </div>
            </Col>
          </Row>
          }
        </Container>
      </section>
    </>
  );
};

export default Tours;
