import React, { useRef, useState, useContext } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCloudUploadAlt } from "@fortawesome/free-solid-svg-icons";
import "../styles/upload-tourguide.css";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "./../context/AuthContext";
import { BASE_URL } from "./../utils/config";

const UploadTourguide = () => {
  const fileInputRef = useRef(null);
  const [selectedFileName, setSelectedFileName] = useState("");

  const [tourData, setTourdata] = useState({
    username: "",
    name: "",
    location: "",
    price: "",
    description: "",
    language: "",
  });

  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFileName(file.name);
  };

  const handleChange = (e) => {
    setTourdata((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${BASE_URL}/tourguide`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(tourData),
      });

      const result = await res.json();

      if (!res.ok) {
        alert(result.message);
      } else {
        dispatch({ type: "LOGIN_SUCCESS" });
        navigate("/tours");
      }
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div className="body">
      <div className="navbar">
        {" "}
        {/* Tambahkan div untuk navbar */}
        {/* Isi navbar */}
      </div>
      <h3 className="Judul__TourGuide">
        Ayo Daftarkan Tour Guide Anda <br />
        untuk menjadi bagian dari TOUR'U
      </h3>
      <form
        className="d-flex flex-column justify-content-center items-center gap-5"
        onSubmit={handleSubmit}
      >
        {/* Form content */}
      </form>
    </div>
  );
};

export default UploadTourguide;
