import React, { useRef, useState, useContext } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCloudUploadAlt } from "@fortawesome/free-solid-svg-icons";
import "../styles/upload-tourguide.css";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "./../context/AuthContext";
import { BASE_URL } from "./../utils/config";

const UploadTourguide = () => {
  const fileInputRef = useRef(null);
  const [selectedFileName, setSelectedFileName] = useState("");

  const [tourData, setTourdata] = useState({
    username: "",
    name: "",
    location: "",
    price: "",
    description: "",
    language: "",
  });

  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFileName(file.name);
  };

  const handleChange = (e) => {
    setTourdata((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${BASE_URL}/tourguide`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(tourData),
      });

      const result = await res.json();

      if (!res.ok) {
        alert(result.message);
      } else {
        dispatch({ type: "LOGIN_SUCCESS" });
        navigate("/tours");
      }
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div className="body">
      <div className="navbar">
        {" "}
        {/* Tambahkan div untuk navbar */}
        {/* Isi navbar */}
      </div>
      <h3 className="Judul__TourGuide">
        Ayo Daftarkan Tour Guide Anda <br />
        untuk menjadi bagian dari TOUR'U
      </h3>
      <form
        className="d-flex flex-column justify-content-center items-center gap-5"
        onSubmit={handleSubmit}
      >
        <div className="d-flex justify-content-center gap-5">
          <div className="form-kiri">
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Username"
              required
              id="username"
              onChange={handleChange}
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Nama Tourguide"
              required
              id="name"
              onChange={handleChange}
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Lokasi"
              required
              id="location"
              onChange={handleChange}
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Harga/Hari"
              required
              id="price"
              onChange={handleChange}
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Deskripsi"
              required
              id="description"
              onChange={handleChange}
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Bahasa"
              required
              id="language"
              onChange={handleChange}
            />
          </div>
          <label className="upload-label" htmlFor="photo">
            <span>{selectedFileName || "Upload Foto Diri"}</span>
            <FontAwesomeIcon icon={faCloudUploadAlt} className="upload-icon" />
            <input
              ref={fileInputRef}
              className="form-control form-control-lg"
              type="file"
              id="photo"
              accept=".png,.jpg,.pdf*"
              onChange={handleFileChange}
            />
          </label>
        </div>
        <div className="m-auto d-flex gap-3">
          <button className="button-submit m-auto rounded-pill" type="submit">
            Submit
          </button>
          <a className="cancel m-auto rounded-pill" href="/tours">
            Cancel
          </a>
        </div>
      </form>
    </div>
  );
};

export default UploadTourguide;
