import React, { useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCloudUploadAlt } from "@fortawesome/free-solid-svg-icons";
import "../styles/upload-homestay.css";

const UploadHomestay = () => {
  const fileInputRef = useRef(null);
  const [selectedFileName, setSelectedFileName] = useState("");
  const [image, setImage] = useState("https://fakeimg.pl/350x200/");
  const [saveImage, setSaveImage] = useState(null);

  function handleUploadChange(e) {
    let uploaded = e.target.files[0];
    setImage(URL.createObjectURL(uploaded));
    setSaveImage(uploaded);
  }

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFileName(file.name);
  };
  function handleSave() {
    if (saveImage) {
      // save image to backend
      let formData = new FormData();
      formData.append("photo", saveImage);

      fetch("http://localhost:4000/api/upload", {
        method: "POST",
        body: formData,
      })
        .then((res) => res.json())
        .then((data) => {
          window.location.href = data.image;
        });
    } else {
      alert("Upload gambar dulu");
    }
  }
  return (
    <div className="body">
      <h3 className="Judul__TourGuide">
        Ayo Daftarkan Home Stay Anda <br />
        untuk menjadi bagian dari TOUR'U
      </h3>
      <form
        action=""
        className="d-flex flex-column justify-content-center items-center gap-5"
      >
        <div className="d-flex justify-content-center gap-5">
          <div className="form-kiri">
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Nama Homestay"
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Lokasi"
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Harga/Hari"
            />
            <input
              className="form-control form-control-lg"
              type="text"
              placeholder="Deskripsi"
            />
          </div>
          <label className="upload-label" htmlFor="formFileLg">
            <span>{selectedFileName || "Upload Foto Homestay"}</span>
            <FontAwesomeIcon icon={faCloudUploadAlt} className="upload-icon" />
            <input
              ref={fileInputRef}
              className="form-control form-control-lg"
              type="file"
              id="formFileLg"
              accept="image/pdf*"
              onChange={handleFileChange}
            />
          </label>
        </div>
        <div className="m-auto d-flex gap-3">
          <button className="button-submit m-auto rounded-pill">Submit</button>
          <a className="cancel m-auto rounded-pill" href="/tours">
            Cancel
          </a>
        </div>
      </form>
    </div>
  );
};

export default UploadHomestay;
